import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';

import LoginForm from './components/LoginForm';
import Dashboard from './components/Dashboard';
import AddStudent from './components/AddStudent';
import ManageVaccination from './components/ManageVaccination';
import GenerateReport from './components/GenerateReport';
import EditDrive from './components/EditDrive';
import NavigationMenu from './components/NavigationMenu';

export default function App() {
  const [token, setToken] = useState('');

  if (!token) {
    return <LoginForm setToken={setToken} />;
  }

  return (
    <Router>
      <div style={{ padding: '2rem' }}>
        <h1>School Vaccination Portal</h1>
        <NavigationMenu />
        <Routes>
          <Route path="/" element={<Navigate to="/dashboard" replace />} />
          <Route path="/dashboard" element={<Dashboard token={token} />} />
          <Route path="/add-student" element={<AddStudent token={token} />} />
          <Route path="/manage-vaccination" element={<ManageVaccination token={token} />} />
          <Route path="/generate-report" element={<GenerateReport token={token} />} />
          <Route path="/edit-drive/:identifier" element={<EditDrive token={token} />} />
        </Routes>
      </div>
    </Router>
  );
}
