import React from 'react';
import axios from '../api/axios';

const GenerateReport = ({ token }) => {
  const handleDownload = async (fileType) => {
    try {
      const response = await axios.get('/register/download', {
        params: { fileDownloadType: fileType },
        responseType: 'blob',
        headers: { Authorization: `Bearer ${token}` }
      });

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `vaccination_report.${fileType.toLowerCase()}`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (error) {
      console.error('Error downloading report:', error);
      alert('Failed to download report.');
    }
  };

  const styles = {
    container: { maxWidth: '400px', margin: '2rem auto', padding: '1.5rem', border: '1px solid #ddd', borderRadius: '8px', backgroundColor: '#fafafa', textAlign: 'center' },
    title: { marginBottom: '1.5rem', fontSize: '1.25rem', fontWeight: '500' },
    button: { margin: '0.5rem', padding: '0.75rem 1.5rem', fontSize: '1rem', border: 'none', borderRadius: '4px', cursor: 'pointer' },
    csvBtn: { backgroundColor: '#007bff', color: 'white' },
    pdfBtn: { backgroundColor: '#28a745', color: 'white' }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Generate Vaccination Report</h2>
      <button
        style={{ ...styles.button, ...styles.csvBtn }}
        onClick={() => handleDownload('CSV')}
      >
        Download CSV
      </button>
      <button
        style={{ ...styles.button, ...styles.pdfBtn }}
        onClick={() => handleDownload('PDF')}
      >
        Download PDF
      </button>
    </div>
  );
};

export default GenerateReport;
