// src/components/GenerateReport.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const GenerateReport = () => {
  const [students, setStudents] = useState([]);
  const [filteredStudents, setFilteredStudents] = useState([]);
  const [vaccineFilter, setVaccineFilter] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [studentsPerPage] = useState(10);

  const fetchStudents = async () => {
    try {
      const response = await axios.get('/register/display', {
        params: {
          pageNo: currentPage - 1, // backend is 0-indexed
          pageSize: studentsPerPage,
        },
      });
      const data = response.data || [];
      setStudents(data);
    } catch (error) {
      console.error('Error fetching student vaccination records:', error);
    }
  };

  useEffect(() => {
    fetchStudents();
  }, [currentPage]);

  useEffect(() => {
    if (!vaccineFilter.trim()) {
      setFilteredStudents(students);
    } else {
      setFilteredStudents(
        students.filter(student =>
          student.vaccineName?.toLowerCase().includes(vaccineFilter.toLowerCase())
        )
      );
    }
  }, [vaccineFilter, students]);

  const handleDownload = async (fileType) => {
    try {
      const response = await axios.get(`/register/download`, {
        params: {
          fileDownloadType: fileType,
        },
        responseType: 'blob',
      });

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `vaccination_report.${fileType.toLowerCase()}`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (error) {
      console.error('Error downloading report:', error);
    }
  };

  const handleFilterChange = (e) => {
    setVaccineFilter(e.target.value);
    setCurrentPage(1);
  };

  const handlePageChange = (direction) => {
    if (direction === 'prev' && currentPage > 1) {
      setCurrentPage(prev => prev - 1);
    } else if (direction === 'next') {
      setCurrentPage(prev => prev + 1);
    }
  };

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <h2 className="text-2xl font-bold mb-6 text-center">Vaccination Report</h2>

      <div className="mb-6 flex flex-col sm:flex-row items-center gap-4">
        <input
          type="text"
          value={vaccineFilter}
          onChange={handleFilterChange}
          placeholder="Filter by Vaccine Name"
          className="border p-2 rounded w-full sm:w-1/2"
        />

        <div className="flex gap-2">
          <button
            onClick={() => handleDownload('CSV')}
            className="bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded"
          >
            Download CSV
          </button>

          <button
            onClick={() => handleDownload('PDF')}
            className="bg-green-600 hover:bg-green-700 text-white font-semibold py-2 px-4 rounded"
          >
            Download PDF
          </button>
        </div>
      </div>

      <div className="overflow-x-auto">
        <table className="min-w-full bg-white border border-gray-200 shadow-sm">
          <thead className="bg-gray-100">
            <tr>
              <th className="py-2 px-4 border">Student Name</th>
              <th className="py-2 px-4 border">Vaccination Status</th>
              <th className="py-2 px-4 border">Vaccination Date</th>
              <th className="py-2 px-4 border">Vaccine Name</th>
            </tr>
          </thead>
          <tbody>
            {filteredStudents.length === 0 ? (
              <tr>
                <td colSpan="4" className="text-center py-4 text-gray-500">
                  No records found.
                </td>
              </tr>
            ) : (
              filteredStudents.map((student, index) => (
                <tr key={index} className="text-center hover:bg-gray-50">
                  <td className="py-2 px-4 border">{student.studentName}</td>
                  <td className="py-2 px-4 border">
                    {student.vaccinationStatus ? 'Vaccinated' : 'Not Vaccinated'}
                  </td>
                  <td className="py-2 px-4 border">
                    {student.vaccinationDate
                      ? new Date(student.vaccinationDate).toLocaleDateString()
                      : 'N/A'}
                  </td>
                  <td className="py-2 px-4 border">{student.vaccineName}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      {/* Pagination */}
      <div className="flex justify-center mt-6 gap-4">
        <button
          onClick={() => handlePageChange('prev')}
          disabled={currentPage === 1}
          className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-semibold py-2 px-4 rounded disabled:opacity-50"
        >
          Prev
        </button>
        <span className="py-2 px-4 font-semibold">{currentPage}</span>
        <button
          onClick={() => handlePageChange('next')}
          className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-semibold py-2 px-4 rounded"
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default GenerateReport;
