import React, { useEffect, useState } from 'react';
import axios from '../api/axios'; // Axios import for API calls

const Dashboard = () => {
  const [vaccinatedCount, setVaccinatedCount] = useState(0);
  const [upcomingDrives, setUpcomingDrives] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAnalyticsData = async () => {
      try {
        // Fetch analytics data for vaccination statistics
        const vaccinationStats = await axios.get('/vaccination-stats');
        setVaccinatedCount(vaccinationStats.data.vaccinatedCount);

        // Fetch upcoming vaccination drives
        const upcomingVaccinationDrives = await axios.get('/upcoming-drives');
        setUpcomingDrives(upcomingVaccinationDrives.data);

        setLoading(false);
      } catch (error) {
        console.error('Error fetching analytics data:', error);
        setLoading(false);
      }
    };

    fetchAnalyticsData();
  }, []);

  return (
    <div className="dashboard">
      <h2>School Coordinator Dashboard</h2>

      {loading ? (
        <p>Loading...</p>
      ) : (
        <>
          <div className="dashboard-stats">
            <div className="stat-box">
              <h3>Students Vaccinated</h3>
              <p>{vaccinatedCount}</p>
            </div>
            <div className="stat-box">
              <h3>Upcoming Vaccination Drives</h3>
              {upcomingDrives.length > 0 ? (
                <ul>
                  {upcomingDrives.map((drive, index) => (
                    <li key={index}>{drive.date} - {drive.location}</li>
                  ))}
                </ul>
              ) : (
                <p>No upcoming drives</p>
              )}
            </div>
          </div>

          <div className="dashboard-menu">
            <h3>Manage</h3>
            <ul>
              <li><a href="/add-student">Add/Manage Students</a></li>
              <li><a href="/manage-vaccination">Manage Vaccination Status</a></li>
              <li><a href="/generate-report">Generate Reports</a></li>
            </ul>
          </div>
        </>
      )}
    </div>
  );
};

export default Dashboard;
