import React, { useEffect, useState } from 'react';
import NavigationMenu from './NavigationMenu';
import axios from '../api/axios'; // Axios instance pointing to your backend

const Dashboard = () => {
  const [analytics, setAnalytics] = useState({
    totalStudents: 0,
    vaccinatedStudents: 0,
    upcomingDrives: 0
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const response = await axios.post('/dashboard/overview');
      const data = response.data;

      setAnalytics({
        totalStudents: data.totalStudents,
        vaccinatedStudents: data.vaccinatedStudents,
        upcomingDrives: data.upcomingDrives
      });

      setLoading(false);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      setLoading(false);
    }
  };

  if (loading) {
    return <div>Loading Dashboard...</div>;
  }

  return (
    <div>
      <NavigationMenu />
      <h2>Dashboard Analytics</h2>
      <div style={{ display: 'flex', justifyContent: 'space-around', marginTop: '20px' }}>
        <div style={{ border: '1px solid gray', padding: '20px', borderRadius: '10px' }}>
          <h3>Total Students</h3>
          <p>{analytics.totalStudents}</p>
        </div>
        <div style={{ border: '1px solid gray', padding: '20px', borderRadius: '10px' }}>
          <h3>Vaccinated Students</h3>
          <p>{analytics.vaccinatedStudents}</p>
        </div>
        <div style={{ border: '1px solid gray', padding: '20px', borderRadius: '10px' }}>
          <h3>Upcoming Drives</h3>
          <p>{analytics.upcomingDrives}</p>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
