// src/App.js
import React, { useState } from 'react';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate
} from 'react-router-dom';

import LoginForm from './components/LoginForm';
import NavigationMenu from './components/NavigationMenu';
import Dashboard from './components/Dashboard';
import AddStudent from './components/AddStudent';
import ManageVaccination from './components/ManageVaccination';
import GenerateReport from './components/GenerateReport';

const App = () => {
  // Persist token so a page refresh keeps you logged in
  const [token, setToken] = useState(localStorage.getItem('token') || '');

  // When login succeeds, store the token
  const handleSetToken = (tok) => {
    localStorage.setItem('token', tok);
    setToken(tok);
  };

  return (
    <Router>
      <div>
        <h1>School Vaccination Portal</h1>

        {!token ? (
          // Show only the login form if no token
          <LoginForm setToken={handleSetToken} />
        ) : (
          <>
            {/* Show the navigation menu once logged in */}
            <NavigationMenu />

            <Routes>
              {/* Redirect root to /dashboard */}
              <Route path="/" element={<Navigate to="/dashboard" replace />} />

              {/* Dashboard route */}
              <Route
                path="/dashboard"
                element={<Dashboard token={token} />}
              />

              {/* Other pages */}
              <Route
                path="/add-student"
                element={<AddStudent token={token} />}
              />
              <Route
                path="/manage-vaccination"
                element={<ManageVaccination token={token} />}
              />
              <Route
                path="/generate-report"
                element={<GenerateReport token={token} />}
              />

              {/* Catch-all: redirect anything else back to dashboard */}
              <Route
                path="*"
                element={<Navigate to="/dashboard" replace />}
              />
            </Routes>
          </>
        )}
      </div>
    </Router>
  );
};

export default App;
