import React, { useState } from 'react';
import axios from '../api/axios';

const AddStudent = ({ token }) => {
  const [form, setForm] = useState({
    identifier: '',
    name: '',
    age: '',
    classId: '',
    mobileNo: '',
    isVaccinated: false
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setSuccess(null);

    const payload = {
      identifier: form.identifier,
      name: form.name,
      age: parseInt(form.age, 10),
      classId: parseInt(form.classId, 10),
      mobileNo: form.mobileNo,
      isVaccinated: form.isVaccinated
    };

    try {
      const res = await axios.post(
        '/v1/student/add',
        payload,
        {
          headers: {
            Authorization: `Bearer ${token}`
          }
        }
      );
      setSuccess('Student added successfully');
      setForm({ identifier: '', name: '', age: '', classId: '', mobileNo: '', isVaccinated: false });
    } catch (err) {
      setError(err.response?.data || 'Failed to add student');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Add / Manage Student</h2>
      <form onSubmit={handleSubmit} style={{ maxWidth: '400px', margin: '0 auto' }}>
        <div>
          <label>Identifier</label>
          <input
            type="text"
            name="identifier"
            value={form.identifier}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Name</label>
          <input
            type="text"
            name="name"
            value={form.name}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Age</label>
          <input
            type="number"
            name="age"
            value={form.age}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Class ID</label>
          <input
            type="number"
            name="classId"
            value={form.classId}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Mobile No</label>
          <input
            type="text"
            name="mobileNo"
            value={form.mobileNo}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>
            <input
              type="checkbox"
              name="isVaccinated"
              checked={form.isVaccinated}
              onChange={handleChange}
            />
            &nbsp;Vaccinated
          </label>
        </div>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        {success && <p style={{ color: 'green' }}>{success}</p>}
        <button type="submit" disabled={loading}>
          {loading ? 'Adding...' : 'Add Student'}
        </button>
      </form>
    </div>
  );
};

export default AddStudent;
