import React, { useState } from 'react';
import LoginForm from './components/LoginForm';
import Dashboard from './components/Dashboard';
import AddStudent from './components/AddStudent';
import ManageVaccination from './components/ManageVaccination';
import GenerateReport from './components/GenerateReport';
import NavigationMenu from './components/NavigationMenu';
import BookDrive from './components/BookDrive';
import AddVaccineDrive from './components/AddVaccineDrive';
import EditDrive from './components/EditDrive';

export default function App() {
  const [token, setToken] = useState('');            // auth token
  const [page, setPage]   = useState('dashboard');   // which view to show
  const [editIdentifier, setEditIdentifier] = useState(null);

  // After login, show portal
  if (!token) {
    return <LoginForm setToken={setToken} />;
  }

  // Determine which component to render
  let Content;
  switch (page) {
    case 'add-student':
      Content = <AddStudent token={token} />;
      break;
    case 'manage-vaccination':
      Content = <ManageVaccination token={token} />;
      break;
    case 'generate-report':
      Content = <GenerateReport token={token} />;
      break;
    case 'book-drive':
      Content = <BookDrive token={token} />;
      break;
    case 'add-vaccine-drive':
      Content = <AddVaccineDrive token={token} />;
      break;
    case 'edit-drive':
      Content = <EditDrive token={token} identifier={editIdentifier} />;
      break;
    default:
      Content = <Dashboard token={token} onEdit={(id) => { setEditIdentifier(id); setPage('edit-drive'); }} />;
      break;
  }

  return (
    <div style={{ padding: '2rem' }}>
      <h1>School Vaccination Portal</h1>
      <NavigationMenu onNavigate={setPage} />
      {Content}
    </div>
  );
}
