import React, { useState, useEffect } from 'react';
import LoginForm from './components/LoginForm';
import axios from './api/axios';

const App = () => {
  const [token, setToken] = useState('');
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (token) {
      fetchDashboardData();
    }
  }, [token]);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      const response = await axios.post('/dashboard/overview');
      setDashboardData(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      setLoading(false);
    }
  };

  return (
    <div>
      <h1>School Vaccination Portal</h1>
      {!token ? (
        <LoginForm setToken={setToken} />
      ) : (
        <div>
          <h2>Dashboard</h2>
          {loading ? (
            <p>Loading dashboard data...</p>
          ) : dashboardData ? (
            <div>
              <p><strong>Total Students:</strong> {dashboardData.totalStudents}</p>
              <p><strong>Students Vaccinated:</strong> {dashboardData.studentsVaccinated}</p>
              <p><strong>Upcoming Drives:</strong> {dashboardData.upcomingDrives}</p>
            </div>
          ) : (
            <p>No dashboard data available.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default App;
