import React, { useState } from 'react';
import axios from '../api/axios';

const ManageVaccination = ({ token }) => {
  const [form, setForm] = useState({
    identifier: '',
    isVaccinated: false
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  const handleChange = e => {
    const { name, value, type, checked } = e.target;
    setForm(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setSuccess(null);

    try {
      await axios.post(
        '/v1/student/update-status',
        { identifier: form.identifier, isVaccinated: form.isVaccinated },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setSuccess('Vaccination status updated successfully.');
      setForm({ identifier: '', isVaccinated: false });
    } catch (err) {
      setError(err.response?.data || 'Failed to update status.');
    } finally {
      setLoading(false);
    }
  };

  const styles = {
    container: { maxWidth: '400px', margin: '2rem auto', padding: '1.5rem', backgroundColor: '#fafafa', borderRadius: '8px', border: '1px solid #ddd' },
    field: { marginBottom: '1rem' },
    label: { display: 'block', marginBottom: '0.5rem', fontWeight: '500' },
    input: { width: '100%', padding: '0.5rem', border: '1px solid #ccc', borderRadius: '4px' },
    checkbox: { marginRight: '0.5rem' },
    button: { padding: '0.75rem 1.5rem', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' },
    messageError: { color: '#f44336', marginBottom: '1rem', textAlign: 'center' },
    messageSuccess: { color: '#4caf50', marginBottom: '1rem', textAlign: 'center' }
  };

  return (
    <div style={styles.container}>
      <h2 style={{ textAlign: 'center', marginBottom: '1.5rem' }}>Manage Vaccination Status</h2>
      <form onSubmit={handleSubmit}>
        <div style={styles.field}>
          <label style={styles.label}>Student Identifier</label>
          <input
            type="text"
            name="identifier"
            value={form.identifier}
            onChange={handleChange}
            required
            style={styles.input}
          />
        </div>
        <div style={styles.field}>
          <label>
            <input
              type="checkbox"
              name="isVaccinated"
              checked={form.isVaccinated}
              onChange={handleChange}
              style={styles.checkbox}
            />
            Vaccinated
          </label>
        </div>
        {error && <div style={styles.messageError}>{error}</div>}
        {success && <div style={styles.messageSuccess}>{success}</div>}
        <button type="submit" disabled={loading} style={styles.button}>
          {loading ? 'Updating...' : 'Update Status'}
        </button>
      </form>
    </div>
  );
};

export default ManageVaccination;
