import React, { useState, useEffect } from 'react';
import NavigationMenu from './NavigationMenu';
import axios from '../api/axios';
import { useNavigate } from 'react-router-dom';

const Dashboard = ({ token }) => {
    const navigate = useNavigate();
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [editingDrive, setEditingDrive] = useState(null);
  const [updatedDrive, setUpdatedDrive] = useState({
    title: '',
    scheduledDate: '',
    location: '',
    availableDozes: '',
    applicableClasses: []
  });

  useEffect(() => {
    if (!token) {
      setLoading(false);
      return;
    }

    const fetchDashboard = async () => {
      setLoading(true);
      setError(null);
      try {
        const res = await axios.post(
          '/dashboard/overview',
          {},
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setData(res.data);
      } catch (e) {
        console.error(e);
        setError('Failed to load dashboard data.');
      } finally {
        setLoading(false);
      }
    };

    fetchDashboard();
  }, [token]);

  const handleEditClick = (drive) => {
    setEditingDrive(drive);
    setUpdatedDrive({
      title: drive.title,
      scheduledDate: drive.scheduledDate,
      location: drive.location,
      availableDozes: drive.availableDozes,
      applicableClasses: drive.applicableClasses
    });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedDrive((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Update API endpoint adjusted to "/v1/vaccine-drive/update/{identifier}"
      const response = await axios.put(
        `/v1/vaccine-drive/update/${editingDrive.driveIdentifier}`, // Using the correct drive identifier
        updatedDrive,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      const updatedVaccinationDrive = response.data;
      const updatedList = data.vaccinationDriveList.map((drive) =>
        drive.id === updatedVaccinationDrive.id ? updatedVaccinationDrive : drive
      );
      setData({ ...data, vaccinationDriveList: updatedList });
      setEditingDrive(null);
    } catch (error) {
      setError('Failed to update vaccination drive.');
    }
  };

  const styles = {
    container: { maxWidth: '800px', margin: '2rem auto', padding: '1.5rem', backgroundColor: '#fafafa', borderRadius: '8px', border: '1px solid #ddd' },
    header: { textAlign: 'center', marginBottom: '1.5rem' },
    statContainer: { display: 'flex', justifyContent: 'space-between', marginBottom: '2rem' },
    statBox: { flex: '1', margin: '0 0.5rem', padding: '1rem', borderRadius: '6px', backgroundColor: 'white', border: '1px solid #ccc', textAlign: 'center' },
    table: { width: '100%', borderCollapse: 'collapse' },
    th: { border: '1px solid #ccc', padding: '10px', backgroundColor: '#f0f0f0', textAlign: 'left' },
    td: { border: '1px solid #ccc', padding: '10px' },
    button: { backgroundColor: '#007bff', color: 'white', padding: '5px 10px', border: 'none', cursor: 'pointer', borderRadius: '4px' }
  };

  return (
    <div style={styles.container}>
      <NavigationMenu onNavigate={() => {}} />
      <h2 style={styles.header}>Dashboard Analytics</h2>

      {/* Loading State */}
      {loading && <p>Loading dashboard data...</p>}

      {/* Stats */}
      <div style={styles.statContainer}>
        <div style={styles.statBox}>
          <h3>Total Students</h3>
          <p>{data ? data.totalStudents : 'Loading...'}</p>
        </div>
        <div style={styles.statBox}>
          <h3>Vaccinated Students</h3>
          <p>{data ? data.vaccinatedStudents : 'Loading...'}</p>
        </div>
        <div style={styles.statBox}>
          <h3>Vaccinated Percentage</h3>
          <p>{data ? data.vaccinatedPercentage : 'Loading...'}</p>
        </div>
      </div>

      {/* Vaccination Drives */}
      <h3>Upcoming Vaccination Drives</h3>
      {data && data.vaccinationDriveList.length > 0 ? (
        <table style={styles.table}>
          <thead>
            <tr>
              <th style={styles.th}>Drive Identifier</th>
              <th style={styles.th}>Title</th>
              <th style={styles.th}>Scheduled Date</th>
              <th style={styles.th}>Location</th>
              <th style={styles.th}>Available Doses</th>
              <th style={styles.th}>Applicable Classes</th>
              <th style={styles.th}>Edit</th>
            </tr>
          </thead>
          <tbody>
            {data.vaccinationDriveList.map((drive) => (
              <tr key={drive.id}>
                <td style={styles.td}>{drive.driveIdentifier}</td>
                <td style={styles.td}>{drive.title}</td>
                <td style={styles.td}>{new Date(drive.scheduledDate).toLocaleString()}</td>
                <td style={styles.td}>{drive.location}</td>
                <td style={styles.td}>{drive.availableDozes}</td>
                <td style={styles.td}>{drive.applicableClasses.join(', ')}</td>
                <td style={styles.td}>
                  <button style={styles.button} onClick={() => handleEditClick(drive)}>
                    Edit
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p style={{ textAlign: 'center', marginTop: '1rem' }}>No vaccination drives available.</p>
      )}

      {/* Edit Form */}
      {editingDrive && (
        <div style={{ marginTop: '2rem' }}>
          <h3>Edit Vaccination Drive</h3>
          <form onSubmit={handleSubmit}>
            <div>
              <label>Title</label>
              <input
                type="text"
                name="title"
                value={updatedDrive.title}
                onChange={handleInputChange}
                required
              />
            </div>
            <div>
              <label>Scheduled Date</label>
              <input
                type="datetime-local"
                name="scheduledDate"
                value={updatedDrive.scheduledDate}
                onChange={handleInputChange}
                required
              />
            </div>
            <div>
              <label>Location</label>
              <input
                type="text"
                name="location"
                value={updatedDrive.location}
                onChange={handleInputChange}
                required
              />
            </div>
            <div>
              <label>Available Doses</label>
              <input
                type="number"
                name="availableDozes"
                value={updatedDrive.availableDozes}
                onChange={handleInputChange}
                required
              />
            </div>
            <div>
              <label>Applicable Classes</label>
              <input
                type="text"
                name="applicableClasses"
                value={updatedDrive.applicableClasses.join(', ')}
                onChange={handleInputChange}
                required
              />
            </div>
            <button type="submit" style={styles.button}>Update</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
