import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import NavigationMenu from './NavigationMenu';
import axios from '../api/axios';

const Dashboard = ({ token }) => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [editingDrive, setEditingDrive] = useState(null);
  const [updatedDrive, setUpdatedDrive] = useState({
    identifier: '',
    name: '',
    title: '',
    scheduledDate: '',
    location: '',
    availableDozes: '',
    applicableClasses: [],
  });

  useEffect(() => {
    if (!token) {
      setLoading(false);
      return;
    }

    const fetchDashboard = async () => {
      setLoading(true);
      setError(null);
      try {
        const res = await axios.post(
          '/dashboard/overview',
          {},
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setData(res.data);
      } catch (e) {
        console.error(e);
        setError('Failed to load dashboard data.');
      } finally {
        setLoading(false);
      }
    };

    fetchDashboard();
  }, [token]);

  const handleEditClick = (drive) => {
    setEditingDrive(drive);
    setUpdatedDrive({
      identifier: drive.identifier,
      name: drive.name,
      title: drive.title,
      scheduledDate: new Date(drive.scheduledDate).toISOString().slice(0, 16),
      location: drive.location,
      availableDozes: drive.availableDozes,
      applicableClasses: drive.applicableClasses,
    });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name === 'applicableClasses') {
      setUpdatedDrive((prev) => ({
        ...prev,
        applicableClasses: value.split(',').map((cls) => cls.trim()),
      }));
    } else {
      setUpdatedDrive((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(
        `/vaccine-drive/update/${editingDrive.identifier}`,
        updatedDrive,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      const updatedList = data.vaccinationDriveList.map((drive) =>
        drive.id === editingDrive.id ? { ...drive, ...updatedDrive } : drive
      );
      setData((prev) => ({ ...prev, vaccinationDriveList: updatedList }));
      setEditingDrive(null);
    } catch (e) {
      console.error(e);
      setError('Failed to update vaccination drive.');
    }
  };

  return (
    <div className="max-w-5xl mx-auto p-6 bg-gray-100 rounded-lg border border-gray-300 animate-in fade-in duration-700">
      <NavigationMenu onNavigate={() => {}} />
      <h2 className="text-3xl font-bold text-center mb-8">Dashboard Analytics</h2>

      {loading && <p>Loading dashboard data...</p>}
      {error && <p className="text-red-600">{error}</p>}

      {data && (
        <>
          <div className="flex flex-col md:flex-row justify-between gap-4 mb-6">
            <div className="flex-1 p-4 bg-white rounded-md border text-center">
              <h3 className="font-semibold text-lg">Total Students</h3>
              <p className="text-2xl">{data.totalStudents}</p>
            </div>
            <div className="flex-1 p-4 bg-white rounded-md border text-center">
              <h3 className="font-semibold text-lg">Vaccinated Students</h3>
              <p className="text-2xl">{data.vaccinatedStudents}</p>
            </div>
            <div className="flex-1 p-4 bg-white rounded-md border text-center">
              <h3 className="font-semibold text-lg">Vaccinated Percentage</h3>
              <p className="text-2xl">{data.vaccinatedPercentage}%</p>
            </div>
          </div>

          <h3 className="text-xl font-semibold mb-4">Upcoming Vaccination Drives</h3>

          {data.vaccinationDriveList.length > 0 ? (
            <div className="overflow-x-auto">
              <table className="table-auto w-full text-left border border-collapse">
                <thead className="bg-gray-200">
                  <tr>
                    <th className="border px-4 py-2">Drive Identifier</th>
                    <th className="border px-4 py-2">Title</th>
                    <th className="border px-4 py-2">Scheduled Date</th>
                    <th className="border px-4 py-2">Location</th>
                    <th className="border px-4 py-2">Available Doses</th>
                    <th className="border px-4 py-2">Applicable Classes</th>
                    <th className="border px-4 py-2">Edit</th>
                  </tr>
                </thead>
                <tbody>
                  {data.vaccinationDriveList.map((drive) => (
                    <tr key={drive.id}>
                      <td className="border px-4 py-2">{drive.driveIdentifier}</td>
                      <td className="border px-4 py-2">{drive.title}</td>
                      <td className="border px-4 py-2">{new Date(drive.scheduledDate).toLocaleString()}</td>
                      <td className="border px-4 py-2">{drive.location}</td>
                      <td className="border px-4 py-2">{drive.availableDozes}</td>
                      <td className="border px-4 py-2">{drive.applicableClasses.join(', ')}</td>
                      <td className="border px-4 py-2">
                        <button
                          onClick={() => handleEditClick(drive)}
                          className="bg-blue-600 text-white px-4 py-1 rounded hover:bg-blue-700"
                        >
                          Edit
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <p className="text-center mt-4">No vaccination drives available.</p>
          )}
        </>
      )}

      {/* Edit Form */}
      {editingDrive && (
        <div className="mt-8 p-6 bg-white border rounded-md animate-in slide-in-from-bottom-6">
          <h3 className="text-xl font-semibold mb-4">Edit Vaccination Drive</h3>
          <form onSubmit={handleSubmit} className="space-y-4">
            {[
              ['Identifier', 'identifier'],
              ['Name', 'name'],
              ['Title', 'title'],
              ['Scheduled Date', 'scheduledDate', 'datetime-local'],
              ['Location', 'location'],
              ['Available Doses', 'availableDozes', 'number'],
              ['Applicable Classes (comma-separated)', 'applicableClasses'],
            ].map(([label, name, type = 'text']) => (
              <div key={name}>
                <label className="block font-medium">{label}</label>
                <input
                  type={type}
                  name={name}
                  value={
                    name === 'applicableClasses'
                      ? updatedDrive.applicableClasses.join(', ')
                      : updatedDrive[name]
                  }
                  onChange={handleInputChange}
                  className="w-full p-2 border rounded mt-1"
                  required
                />
              </div>
            ))}
            <button
              type="submit"
              className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
            >
              Update Drive
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
