import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import NavigationMenu from './NavigationMenu';
import axios from '../api/axios';

const Dashboard = ({ token }) => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [editingDrive, setEditingDrive] = useState(null);
  const [updatedDrive, setUpdatedDrive] = useState({
    identifier: '',
    name: '',
    title: '',
    scheduledDate: '',
    location: '',
    availableDozes: '',
    applicableClasses: []
  });

  useEffect(() => {
    if (!token) {
      setLoading(false);
      return;
    }

    const fetchDashboard = async () => {
      setLoading(true);
      setError(null);
      try {
        const res = await axios.post(
          '/dashboard/overview',
          {},
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setData(res.data);
      } catch (e) {
        console.error(e);
        setError('Failed to load dashboard data.');
      } finally {
        setLoading(false);
      }
    };

    fetchDashboard();
  }, [token]);

  const handleEditClick = (drive) => {
    setEditingDrive(drive);
    setUpdatedDrive({
      identifier: drive.identifier,
      name: drive.name,
      title: drive.title,
      scheduledDate: new Date(drive.scheduledDate).toISOString().slice(0, 16),
      location: drive.location,
      availableDozes: drive.availableDozes,
      applicableClasses: drive.applicableClasses
    });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name === 'applicableClasses') {
      setUpdatedDrive((prev) => ({
        ...prev,
        applicableClasses: value.split(',').map(cls => cls.trim())
      }));
    } else {
      setUpdatedDrive((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(
        `/vaccine-drive/update/${editingDrive.identifier}`,
        updatedDrive,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      const updatedList = data.vaccinationDriveList.map((drive) =>
        drive.id === editingDrive.id ? { ...drive, ...updatedDrive } : drive
      );
      setData((prev) => ({ ...prev, vaccinationDriveList: updatedList }));
      setEditingDrive(null);
    } catch (e) {
      console.error(e);
      setError('Failed to update vaccination drive.');
    }
  };

  return (
    <div className="container mx-auto p-6 bg-blue-50 rounded-lg shadow-md">
      <NavigationMenu onNavigate={() => {}} />
      <h2 className="text-2xl font-bold text-center text-gray-800 mb-6 bg-blue-100 p-4 rounded-lg">Dashboard Analytics</h2>

      {loading && <p className="text-center text-gray-600">Loading dashboard data...</p>}
      {error && <p className="text-center text-red-500">{error}</p>}

      {data && (
        <>
          <div className="grid grid-cols-1 sm:grid-cols-3 gap-4 mb-6">
            <div className="bg-white p-4 rounded-lg shadow-sm text-center bg-green-100">
              <h3 className="text-lg font-semibold text-gray-700">Total Students</h3>
              <p className="text-2xl text-gray-900">{data.totalStudents}</p>
            </div>
            <div className="bg-white p-4 rounded-lg shadow-sm text-center bg-yellow-100">
              <h3 className="text-lg font-semibold text-gray-700">Vaccinated Students</h3>
              <p className="text-2xl text-gray-900">{data.vaccinatedStudents}</p>
            </div>
            <div className="bg-white p-4 rounded-lg shadow-sm text-center bg-purple-100">
              <h3 className="text-lg font-semibold text-gray-700">Vaccinated Percentage</h3>
              <p className="text-2xl text-gray-900">{data.vaccinatedPercentage}%</p>
            </div>
          </div>

          <h3 className="text-xl font-semibold text-gray-800 mb-4 bg-blue-200 p-3 rounded-lg">Upcoming Vaccination Drives</h3>
          {data.vaccinationDriveList.length > 0 ? (
            <table className="min-w-full table-auto bg-white rounded-lg shadow-md">
              <thead className="bg-gray-100 text-gray-700">
                <tr>
                  <th className="px-6 py-3 text-left">Drive Identifier</th>
                  <th className="px-6 py-3 text-left">Title</th>
                  <th className="px-6 py-3 text-left">Scheduled Date</th>
                  <th className="px-6 py-3 text-left">Location</th>
                  <th className="px-6 py-3 text-left">Available Doses</th>
                  <th className="px-6 py-3 text-left">Applicable Classes</th>
                  <th className="px-6 py-3 text-left">Edit</th>
                </tr>
              </thead>
              <tbody>
                {data.vaccinationDriveList.map((drive) => (
                  <tr key={drive.id} className="border-b hover:bg-gray-50">
                    <td className="px-6 py-4">{drive.driveIdentifier}</td>
                    <td className="px-6 py-4">{drive.title}</td>
                    <td className="px-6 py-4">{new Date(drive.scheduledDate).toLocaleString()}</td>
                    <td className="px-6 py-4">{drive.location}</td>
                    <td className="px-6 py-4">{drive.availableDozes}</td>
                    <td className="px-6 py-4">{drive.applicableClasses.join(', ')}</td>
                    <td className="px-6 py-4 text-center">
                      <button
                        className="bg-blue-500 text-white py-2 px-4 rounded-lg hover:bg-blue-600"
                        onClick={() => handleEditClick(drive)}
                      >
                        Edit
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p className="text-center text-gray-600 mt-4">No vaccination drives available.</p>
          )}
        </>
      )}

      {/* Edit Form */}
      {editingDrive && (
        <div className="mt-8 bg-blue-50 p-6 rounded-lg shadow-md">
          <h3 className="text-xl font-semibold text-gray-800 mb-4">Edit Vaccination Drive</h3>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="block text-gray-700">Identifier</label>
              <input
                type="text"
                name="identifier"
                value={updatedDrive.identifier}
                onChange={handleInputChange}
                className="w-full p-3 border border-gray-300 rounded-lg mt-2"
                required
              />
            </div>
            <div>
              <label className="block text-gray-700">Name</label>
              <input
                type="text"
                name="name"
                value={updatedDrive.name}
                onChange={handleInputChange}
                className="w-full p-3 border border-gray-300 rounded-lg mt-2"
                required
              />
            </div>
            <div>
              <label className="block text-gray-700">Title</label>
              <input
                type="text"
                name="title"
                value={updatedDrive.title}
                onChange={handleInputChange}
                className="w-full p-3 border border-gray-300 rounded-lg mt-2"
                required
              />
            </div>
            <div>
              <label className="block text-gray-700">Scheduled Date</label>
              <input
                type="datetime-local"
                name="scheduledDate"
                value={updatedDrive.scheduledDate}
                onChange={handleInputChange}
                className="w-full p-3 border border-gray-300 rounded-lg mt-2"
                required
              />
            </div>
            <div>
              <label className="block text-gray-700">Location</label>
              <input
                type="text"
                name="location"
                value={updatedDrive.location}
                onChange={handleInputChange}
                className="w-full p-3 border border-gray-300 rounded-lg mt-2"
                required
              />
            </div>
            <div>
              <label className="block text-gray-700">Available Doses</label>
              <input
                type="number"
                name="availableDozes"
                value={updatedDrive.availableDozes}
                onChange={handleInputChange}
                className="w-full p-3 border border-gray-300 rounded-lg mt-2"
                required
              />
            </div>
            <div>
              <label className="block text-gray-700">Applicable Classes (comma-separated)</label>
              <input
                type="text"
                name="applicableClasses"
                value={updatedDrive.applicableClasses.join(', ')}
                onChange={handleInputChange}
                className="w-full p-3 border border-gray-300 rounded-lg mt-2"
                required
              />
            </div>
            <button type="submit" className="w-full py-3 bg-blue-500 text-white rounded-lg hover:bg-blue-600">
              Update Drive
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
