import React, { useState } from 'react';
import LoginForm from './components/LoginForm';
import Dashboard from './components/Dashboard';
import AddStudent from './components/AddStudent';
import ManageVaccination from './components/ManageVaccination';
import GenerateReport from './components/GenerateReport';
import NavigationMenu from './components/NavigationMenu';

export default function App() {
  const [token, setToken] = useState('');            // your auth token
  const [page, setPage]   = useState('dashboard');   // which view to show

  if (!token) {
    return <LoginForm setToken={setToken} />;
  }

  let Content;
  switch (page) {
    case 'add-student':         Content = <AddStudent token={token} />; break;
    case 'manage-vaccination':  Content = <ManageVaccination token={token} />; break;
    case 'generate-report':     Content = <GenerateReport token={token} />; break;

    default:                    Content = <Dashboard token={token} />; break;
  }

  return (
    <div style={{ padding: '2rem' }}>
      <h1>School Vaccination Portal</h1>
      <NavigationMenu onNavigate={setPage} />
      {Content}
    </div>
  );
}
