import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const Container = styled.div`
  padding: 2rem;
  max-width: 1200px;
  margin: 0 auto;
`;

const Table = styled.table`
  width: 100%;
  border-collapse: collapse;
  margin-top: 2rem;
`;

const TableHeader = styled.th`
  background-color: #007bff;
  color: white;
  padding: 10px;
  font-size: 1rem;
`;

const TableCell = styled.td`
  padding: 10px;
  border: 1px solid #ddd;
`;

const Button = styled.button`
  background-color: #28a745;
  color: white;
  padding: 10px 20px;
  border: none;
  border-radius: 5px;
  margin: 10px;
  cursor: pointer;

  &:hover {
    background-color: #218838;
  }
`;

const GenerateReport = () => {
  const [records, setRecords] = useState([]);
  const [vaccineFilter, setVaccineFilter] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get('/register/display');
        setRecords(res.data || []);
      } catch (err) {
        console.error('Error loading data:', err);
      }
    };
    fetchData();
  }, []);

  const filtered = records.filter((rec) =>
    rec.vaccineName?.toLowerCase().includes(vaccineFilter.toLowerCase())
  );

  return (
    <Container>
      <h2>Vaccination Report</h2>
      <div>
        <input
          type="text"
          value={vaccineFilter}
          onChange={(e) => setVaccineFilter(e.target.value)}
          placeholder="Filter by Vaccine Name"
        />
        <Button onClick={() => handleDownload('CSV')}>Download CSV</Button>
        <Button onClick={() => handleDownload('PDF')}>Download PDF</Button>
      </div>

      <Table>
        <thead>
          <tr>
            <TableHeader>Student Name</TableHeader>
            <TableHeader>Vaccination Status</TableHeader>
            <TableHeader>Vaccination Date</TableHeader>
            <TableHeader>Vaccine Name</TableHeader>
          </tr>
        </thead>
        <tbody>
          {filtered.length === 0 ? (
            <tr>
              <TableCell colSpan="4">No records found</TableCell>
            </tr>
          ) : (
            filtered.map((r, i) => (
              <tr key={i}>
                <TableCell>{r.studentName}</TableCell>
                <TableCell>{r.vaccinationStatus ? 'Vaccinated' : 'Not Vaccinated'}</TableCell>
                <TableCell>{r.vaccinationDate ? new Date(r.vaccinationDate).toLocaleDateString() : 'N/A'}</TableCell>
                <TableCell>{r.vaccineName}</TableCell>
              </tr>
            ))
          )}
        </tbody>
      </Table>
    </Container>
  );
};

export default GenerateReport;
