import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom'; // Import Router
import LoginForm from './components/LoginForm';
import Dashboard from './components/Dashboard';
import NavigationMenu from './components/NavigationMenu'; // Include the NavigationMenu
import axios from './api/axios';

const App = () => {
  const [token, setToken] = useState('');

  return (
    <Router>
      <div>
        <h1>School Vaccination Portal</h1>
        {!token ? (
          <LoginForm setToken={setToken} />
        ) : (
          <>
            {/* Navigation Menu */}
            <NavigationMenu />

            {/* Dashboard Content */}
            <Switch>
              <Route
                exact
                path="/dashboard"
                render={() => <Dashboard token={token} />}
              />
              {/* Add other routes like /add-student, /manage-vaccination, etc */}
            </Switch>
          </>
        )}
      </div>
    </Router>
  );
};

export default App;
