import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import NavigationMenu from './NavigationMenu';
import axios from '../api/axios';

const Dashboard = ({ token }) => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    if (!token) {
      setLoading(false);
      return;
    }

    const fetchDashboard = async () => {
      setLoading(true);
      setError(null);
      try {
        const res = await axios.post(
          '/dashboard/overview',
          {},
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setData(res.data);
      } catch (e) {
        console.error(e);
        setError('Failed to load dashboard data.');
      } finally {
        setLoading(false);
      }
    };

    fetchDashboard();
  }, [token]);

  const handleEditClick = (driveIdentifier) => {
    navigate(`/edit-drive/${driveIdentifier}`);
  };

  const styles = {
    container: {
      maxWidth: '900px',
      margin: '2rem auto',
      padding: '1.5rem',
      backgroundColor: '#fafafa',
      borderRadius: '8px',
      border: '1px solid #ddd',
    },
    header: { textAlign: 'center', marginBottom: '2rem' },
    statContainer: {
      display: 'flex',
      justifyContent: 'space-between',
      marginBottom: '2rem',
    },
    statBox: {
      flex: '1',
      margin: '0 0.5rem',
      padding: '1rem',
      borderRadius: '8px',
      backgroundColor: 'white',
      border: '1px solid #ccc',
      textAlign: 'center',
    },
    table: {
      width: '100%',
      borderCollapse: 'collapse',
      marginTop: '1rem',
    },
    th: {
      border: '1px solid #ccc',
      padding: '10px',
      backgroundColor: '#f0f0f0',
      textAlign: 'left',
    },
    td: {
      border: '1px solid #ccc',
      padding: '10px',
    },
    editButton: {
      backgroundColor: '#007bff',
      color: 'white',
      padding: '8px 12px',
      border: 'none',
      cursor: 'pointer',
      borderRadius: '5px',
      fontWeight: 'bold',
    },
  };

  return (
    <>
      <NavigationMenu />
      <div style={styles.container}>
        <h2 style={styles.header}>Dashboard Analytics</h2>

        {loading && <p>Loading dashboard data...</p>}
        {error && <p style={{ color: 'red' }}>{error}</p>}

        {data && (
          <>
            <div style={styles.statContainer}>
              <div style={styles.statBox}>
                <h3>Total Students</h3>
                <p>{data.totalStudents ?? 0}</p>
              </div>
              <div style={styles.statBox}>
                <h3>Vaccinated Students</h3>
                <p>{data.vaccinatedStudents ?? 0}</p>
              </div>
              <div style={styles.statBox}>
                <h3>Vaccinated Percentage</h3>
                <p>{data.vaccinatedPercentage ?? 0}%</p>
              </div>
            </div>

            <h3>Upcoming Vaccination Drives</h3>
            {Array.isArray(data.vaccinationDriveList) && data.vaccinationDriveList.length > 0 ? (
              <table style={styles.table}>
                <thead>
                  <tr>
                    <th style={styles.th}>Drive Identifier</th>
                    <th style={styles.th}>Title</th>
                    <th style={styles.th}>Scheduled Date</th>
                    <th style={styles.th}>Location</th>
                    <th style={styles.th}>Available Doses</th>
                    <th style={styles.th}>Applicable Classes</th>
                    <th style={styles.th}>Edit</th>
                  </tr>
                </thead>
                <tbody>
                  {data.vaccinationDriveList.map((drive) => (
                    <tr key={drive.id}>
                      <td style={styles.td}>{drive.driveIdentifier}</td>
                      <td style={styles.td}>{drive.title}</td>
                      <td style={styles.td}>
                        {new Date(drive.scheduledDate).toLocaleDateString(undefined, {
                          year: 'numeric',
                          month: 'short',
                          day: 'numeric',
                          hour: '2-digit',
                          minute: '2-digit',
                        })}
                      </td>
                      <td style={styles.td}>{drive.location}</td>
                      <td style={styles.td}>{drive.availableDoses ?? 0}</td>
                      <td style={styles.td}>{(drive.applicableClasses || []).join(', ')}</td>
                      <td style={styles.td}>
                        <button
                          style={styles.editButton}
                          onClick={() => handleEditClick(drive.driveIdentifier)}
                        >
                          Edit
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            ) : (
              <p style={{ textAlign: 'center', marginTop: '1rem' }}>
                No vaccination drives available.
              </p>
            )}
          </>
        )}
      </div>
    </>
  );
};

export default Dashboard;
